insert into book_tag(book_id,tag_key,tag_value) values (2,'description','Roman napisan 1939. na Kubi a objavljen 1940., jedno od piščevih najpopularnijih dela. To je istorijsko - ljubavni roman koji govori o doživljajima američkog profesora španskog jezika Roberta Jordana, koji učestvuje u Španskom građanskom ratu kao dobrovoljac protiv fašista Francisca Franca, a na strani komunista. Sam roman bazira se na Hemingwayevim iskustvima u Španiji tokom rata.');
