insert into book_tag(book_id,tag_key,tag_value) values (3,'description','Američki dramski roman koji je 1960. napisala i objavila Harper Lee. Postigao je brzi uspeh i postao klasik američke književnosti. Radnja se labavo temelji na Leeinim opservacijama o svojoj porodici i komšijama, kao i na događaju koji se je odigrao u blizini njenog grada 1936., kada je imala 10 godina. Roman krasi toplina i humor, usprkos tome što se bavi ozbiljnim temama o silovanju i rasnoj diskriminaciji.');
