insert into book_tag(book_id,tag_key,tag_value) values (4,'description','Proces je roman Franza Kafke, napisan između 1914. i 1915. godine i objavljen 1925. godine. Jedan od njegovih najpoznatijih radova, to je priča o čoveku koji je uhapšen i optužen od strane dalekog i nedostupnog organa vlasti, za zločin čija priroda ostaje do kraja nepoznata i njemu i čitaocu.');